-- Enable UUID extension
CREATE EXTENSION IF NOT EXISTS "uuid-ossp";

-- Create Users table
CREATE TABLE "Users" (
    "Id" UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    "Email" VARCHAR(255) NOT NULL UNIQUE,
    "PasswordHash" VARCHAR(500) NOT NULL,
    "Name" VARCHAR(100) NOT NULL,
    "CreatedAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    "UpdatedAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    "IsActive" BOOLEAN NOT NULL DEFAULT TRUE
);

-- Create index on Email for faster lookups
CREATE INDEX "IX_Users_Email" ON "Users" ("Email");

-- Create Bets table
CREATE TABLE "Bets" (
    "Id" UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    "UserId" UUID NOT NULL,
    "Match" VARCHAR(200) NOT NULL,
    "BetType" VARCHAR(50) NOT NULL,
    "Odds" DECIMAL(10,2) NOT NULL CHECK ("Odds" > 0),
    "Stake" DECIMAL(10,2) NOT NULL CHECK ("Stake" > 0),
    "PotentialPayout" DECIMAL(10,2) NOT NULL CHECK ("PotentialPayout" > 0),
    "Status" VARCHAR(20) NOT NULL DEFAULT 'Pending',
    "Result" VARCHAR(20),
    "ActualPayout" DECIMAL(10,2) DEFAULT 0,
    "BetDate" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    "MatchDate" TIMESTAMP WITH TIME ZONE NOT NULL,
    "Notes" TEXT,
    "CreatedAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    "UpdatedAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    
    -- Foreign key constraint
    CONSTRAINT "FK_Bets_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE CASCADE
);

-- Create indexes for better performance
CREATE INDEX "IX_Bets_UserId" ON "Bets" ("UserId");
CREATE INDEX "IX_Bets_Status" ON "Bets" ("Status");
CREATE INDEX "IX_Bets_BetDate" ON "Bets" ("BetDate");
CREATE INDEX "IX_Bets_MatchDate" ON "Bets" ("MatchDate");

-- Create RefreshTokens table
CREATE TABLE "RefreshTokens" (
    "Id" UUID PRIMARY KEY DEFAULT uuid_generate_v4(),
    "UserId" UUID NOT NULL,
    "Token" VARCHAR(500) NOT NULL UNIQUE,
    "ExpiryDate" TIMESTAMP WITH TIME ZONE NOT NULL,
    "IsRevoked" BOOLEAN NOT NULL DEFAULT FALSE,
    "CreatedAt" TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT NOW(),
    "RevokedAt" TIMESTAMP WITH TIME ZONE,
    
    -- Foreign key constraint
    CONSTRAINT "FK_RefreshTokens_Users_UserId" FOREIGN KEY ("UserId") REFERENCES "Users" ("Id") ON DELETE CASCADE
);

-- Create indexes for refresh tokens
CREATE INDEX "IX_RefreshTokens_UserId" ON "RefreshTokens" ("UserId");
CREATE INDEX "IX_RefreshTokens_Token" ON "RefreshTokens" ("Token");
CREATE INDEX "IX_RefreshTokens_ExpiryDate" ON "RefreshTokens" ("ExpiryDate");

-- Add check constraints for bet status
ALTER TABLE "Bets" ADD CONSTRAINT "CK_Bets_Status" 
    CHECK ("Status" IN ('Pending', 'Won', 'Lost', 'Cancelled', 'Void'));

-- Add check constraint for bet result
ALTER TABLE "Bets" ADD CONSTRAINT "CK_Bets_Result" 
    CHECK ("Result" IS NULL OR "Result" IN ('Win', 'Loss', 'Void'));

-- Add check constraint for bet type
ALTER TABLE "Bets" ADD CONSTRAINT "CK_Bets_BetType" 
    CHECK ("BetType" IN ('1X2', 'Over/Under', 'Both Teams to Score', 'Handicap', 'Correct Score', 'Double Chance', 'Draw No Bet', 'Total Goals'));

-- Create function to update UpdatedAt timestamp
CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW."UpdatedAt" = NOW();
    RETURN NEW;
END;
$$ language 'plpgsql';

-- Create triggers to automatically update UpdatedAt
CREATE TRIGGER update_users_updated_at 
    BEFORE UPDATE ON "Users" 
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

CREATE TRIGGER update_bets_updated_at 
    BEFORE UPDATE ON "Bets" 
    FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

-- Create view for bet statistics
CREATE VIEW "BetStatistics" AS
SELECT 
    u."Id" as "UserId",
    u."Name" as "UserName",
    COUNT(b."Id") as "TotalBets",
    COUNT(CASE WHEN b."Status" = 'Won' THEN 1 END) as "WonBets",
    COUNT(CASE WHEN b."Status" = 'Lost' THEN 1 END) as "LostBets",
    COUNT(CASE WHEN b."Status" = 'Pending' THEN 1 END) as "PendingBets",
    COALESCE(SUM(b."Stake"), 0) as "TotalStake",
    COALESCE(SUM(b."ActualPayout"), 0) as "TotalPayout",
    COALESCE(SUM(b."ActualPayout") - SUM(b."Stake"), 0) as "ProfitLoss",
    CASE 
        WHEN COUNT(CASE WHEN b."Status" IN ('Won', 'Lost') THEN 1 END) > 0 
        THEN ROUND(
            (COUNT(CASE WHEN b."Status" = 'Won' THEN 1 END)::DECIMAL / 
             COUNT(CASE WHEN b."Status" IN ('Won', 'Lost') THEN 1 END)::DECIMAL) * 100, 2
        )
        ELSE 0 
    END as "WinRate",
    CASE 
        WHEN SUM(b."Stake") > 0 
        THEN ROUND(((SUM(b."ActualPayout") - SUM(b."Stake")) / SUM(b."Stake")) * 100, 2)
        ELSE 0 
    END as "ROI"
FROM "Users" u
LEFT JOIN "Bets" b ON u."Id" = b."UserId"
GROUP BY u."Id", u."Name";

